import React, { useState, useCallback, useContext } from 'react';

import "./Calculator.css";

const CalculatorContext = React.createContext();

// Define the Calculator Provider
const CalculatorProvider = ({ children }) => {
  const [input, setInput] = useState('');
  const [result, setResult] = useState(0);

  // Define the evaluate function
  const evaluate = useCallback(() => {
    try {
      const evaluated = eval(input);
      setResult(evaluated);
    } catch (err) {
      setResult('Error');
    }
  }, [input]);

  // Define the clear function
  const clear = useCallback(() => {
    setInput('');
    setResult(0);
  }, []);

  // Define the updateInput function
  const updateInput = useCallback((value) => {
    setInput((input) => input + value);
  }, []);

  // Define the context value
  const value = {
    input,
    setInput,
    result,
    setResult,
    evaluate,
    clear,
    updateInput,
  };

  return (
    <CalculatorContext.Provider value={value}>
      {children}
    </CalculatorContext.Provider>
  );
};

// Define the Display Component
const Display = () => {
  const { input, result } = useContext(CalculatorContext);

  return (
    <div className="display">
      <div className="input">{input}</div>
      <div className="result">{result}</div>
    </div>
  );
};

// Define the Button Component
const Button = ({ label, value }) => {
  const { updateInput } = useContext(CalculatorContext);

  return (
    <button className="button" onClick={() => updateInput(value)}>
      {label}
    </button>
  );
};

// Define the Buttons Component
const Buttons = () => {
  return (
    <div className="buttons">
      <Button label="7" value="7" />
      <Button label="8" value="8" />
      <Button label="9" value="9" />
      <Button label="/" value="/" />
      <Button label="4" value="4" />
      <Button label="5" value="5" />
      <Button label="6" value="6" />
      <Button label="*" value="*" />
      <Button label="1" value="1" />
      <Button label="2" value="2" />
      <Button label="3" value="3" />
      <Button label="-" value="-" />
      <Button label="0" value="0" />
      <Button label="." value="." />
      <Button label="=" value="=" />
      <Button label="+" value="+" />
    </div>
  );
};

// Define the Calculator Component
const Calculator = () => {
  return (
    <div className="calculator">
      <Display />
      <Buttons />
    </div>
  );
};


function App() {
  return (
    <CalculatorProvider>
      <Calculator />
    </CalculatorProvider>
  );
}

export default App;
